% To run tests:
% erl -make
% erl -noshell -eval "eunit:test(not_quite_lisp, [verbose])" -s init stop
%

-module(not_quite_lisp_tests).
-import(not_quite_lisp, [floor/1, floors/1, basement/1]).
-include_lib("eunit/include/eunit.hrl").

floor_test_zero() ->
  ?assertEqual(0, floor("((()))")).

floor_test_other_zero() ->
  ?assertEqual(0, floor("()()")).

floor_test_three() ->
  ?assertEqual(3, floor("(((")).

floor_test_other_three() ->
  ?assertEqual(3, floor("(()(()(")).

floor_test_other_other_three() ->
  ?assertEqual(3, floor("))(((((")).

floor_test_minus() ->
  ?assertEqual(-1, floor("())")).

floor_test_other_minus() ->
  ?assertEqual(-1, floor("))(")).

floor_test_another_minus() ->
  ?assertEqual(-3, floor(")))")).

floor_test_another_nother_minus() ->
  ?assertEqual(-3, floor(")())())")).

